import React, { useState, useEffect, useRef } from 'react';
import { FiX, FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import './Portfolio.css';

// Import fallback photos
import photo1 from '../Photos/DSC00390.jpg';
import photo2 from '../Photos/DSC04635.jpg';
import photo3 from '../Photos/DSC04724.jpg';
import photo4 from '../Photos/DSC04875.jpg';

const Portfolio = () => {
  const [photos, setPhotos] = useState([]);
  const [lightbox, setLightbox] = useState({ open: false, index: 0 });
  const [visibleItems, setVisibleItems] = useState(new Set());
  const itemRefs = useRef({});

  // Fallback photos if none uploaded
  const fallbackPhotos = [
    { id: 1, url: photo1 },
    { id: 2, url: photo2 },
    { id: 3, url: photo3 },
    { id: 4, url: photo4 },
    { id: 5, url: photo1 },
    { id: 6, url: photo2 },
    { id: 7, url: photo3 },
    { id: 8, url: photo4 },
    { id: 9, url: photo1 },
    { id: 10, url: photo2 },
    { id: 11, url: photo3 },
    { id: 12, url: photo4 },
  ];

  useEffect(() => {
    // Load photos from localStorage (admin panel uploads)
    const savedPhotos = localStorage.getItem('portfolio_photos');
    if (savedPhotos) {
      const parsed = JSON.parse(savedPhotos);
      if (parsed.length > 0) {
        setPhotos(parsed);
        return;
      }
    }
    // Use fallback photos
    setPhotos(fallbackPhotos);
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setVisibleItems((prev) => new Set([...prev, entry.target.dataset.id]));
          }
        });
      },
      { threshold: 0.1, rootMargin: '100px' }
    );

    const timeoutId = setTimeout(() => {
      Object.values(itemRefs.current).forEach((ref) => {
        if (ref) observer.observe(ref);
      });
    }, 100);

    return () => {
      clearTimeout(timeoutId);
      observer.disconnect();
    };
  }, [photos]);

  const openLightbox = (index) => {
    setLightbox({ open: true, index });
    document.body.style.overflow = 'hidden';
  };

  const closeLightbox = () => {
    setLightbox({ open: false, index: 0 });
    document.body.style.overflow = 'unset';
  };

  const navigateLightbox = (direction) => {
    let newIndex = lightbox.index + direction;
    if (newIndex < 0) newIndex = photos.length - 1;
    if (newIndex >= photos.length) newIndex = 0;
    setLightbox({ open: true, index: newIndex });
  };

  useEffect(() => {
    if (!lightbox.open) return;

    const handleKeyPress = (e) => {
      if (e.key === 'Escape') closeLightbox();
      if (e.key === 'ArrowLeft') navigateLightbox(-1);
      if (e.key === 'ArrowRight') navigateLightbox(1);
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [lightbox]);

  // Create artistic layout pattern
  const getItemClass = (index) => {
    const pattern = index % 12;
    if (pattern === 0) return 'item-large';
    if (pattern === 3) return 'item-tall';
    if (pattern === 5) return 'item-wide';
    if (pattern === 8) return 'item-large';
    if (pattern === 11) return 'item-tall';
    return 'item-normal';
  };

  return (
    <div className="portfolio-page">
      <div className="portfolio-masonry">
        {photos.map((photo, index) => (
          <div
            key={photo.id}
            ref={(el) => (itemRefs.current[photo.id] = el)}
            data-id={photo.id}
            className={`masonry-item ${getItemClass(index)} ${visibleItems.has(String(photo.id)) ? 'visible' : ''}`}
            style={{ animationDelay: `${(index % 6) * 0.1}s` }}
            onClick={() => openLightbox(index)}
          >
            <img src={photo.url} alt="" loading="lazy" />
            <div className="item-overlay"></div>
          </div>
        ))}
      </div>

      {lightbox.open && photos[lightbox.index] && (
        <div className="portfolio-lightbox" onClick={closeLightbox}>
          <button className="lightbox-close" onClick={closeLightbox}>
            <FiX />
          </button>
          <button 
            className="lightbox-nav lightbox-prev"
            onClick={(e) => { e.stopPropagation(); navigateLightbox(-1); }}
          >
            <FiChevronLeft />
          </button>
          <button 
            className="lightbox-nav lightbox-next"
            onClick={(e) => { e.stopPropagation(); navigateLightbox(1); }}
          >
            <FiChevronRight />
          </button>
          <div className="lightbox-content" onClick={(e) => e.stopPropagation()}>
            <img 
              src={photos[lightbox.index].url} 
              alt=""
            />
          </div>
          <div className="lightbox-counter">
            {lightbox.index + 1} / {photos.length}
          </div>
        </div>
      )}
    </div>
  );
};

export default Portfolio;
