import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FiMenu, FiX } from 'react-icons/fi';
import logoImage from '../Photos/Kreslici-platno-1_3@-e1653722976268.webp';
import './Navbar.css';

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const isActive = (path) => location.pathname === path;

  return (
    <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">
          <img src={logoImage} alt="Logo" className="logo-image" />
        </Link>

        <div className={`navbar-menu ${isMobileMenuOpen ? 'active' : ''}`}>
          <Link 
            to="/gallery?category=personal" 
            className={`navbar-link ${location.pathname === '/gallery' && location.search.includes('personal') ? 'active' : ''}`}
            onClick={() => setIsMobileMenuOpen(false)}
          >
            Personal Photoshoot
          </Link>
          <Link 
            to="/gallery?category=video-fashion" 
            className={`navbar-link ${location.pathname === '/gallery' && location.search.includes('video-fashion') ? 'active' : ''}`}
            onClick={() => setIsMobileMenuOpen(false)}
          >
            Video: Fashion/Beauty Campaigns
          </Link>
          <Link 
            to="/gallery?category=photo-fashion" 
            className={`navbar-link ${location.pathname === '/gallery' && location.search.includes('photo-fashion') ? 'active' : ''}`}
            onClick={() => setIsMobileMenuOpen(false)}
          >
            Photo: Fashion/Beauty Campaigns
          </Link>
          <Link 
            to="/gallery?category=reels" 
            className={`navbar-link ${location.pathname === '/gallery' && location.search.includes('reels') ? 'active' : ''}`}
            onClick={() => setIsMobileMenuOpen(false)}
          >
            Reels
          </Link>
          <Link 
            to="/gallery?category=weddings" 
            className={`navbar-link ${location.pathname === '/gallery' && location.search.includes('weddings') ? 'active' : ''}`}
            onClick={() => setIsMobileMenuOpen(false)}
          >
            Weddings
          </Link>
        </div>

        <button 
          className="navbar-toggle"
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          aria-label="Toggle menu"
        >
          {isMobileMenuOpen ? <FiX /> : <FiMenu />}
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
