import React, { useState, useEffect } from 'react';
import { FiUpload, FiImage, FiVideo, FiTrash2, FiEdit2, FiX } from 'react-icons/fi';
import toast from 'react-hot-toast';
import './Admin.css';

const Admin = () => {
  const [activeTab, setActiveTab] = useState('photos');
  const [photos, setPhotos] = useState([]);
  const [videos, setVideos] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [editingItem, setEditingItem] = useState(null);

  // Load existing media from localStorage
  useEffect(() => {
    const savedPhotos = localStorage.getItem('portfolio_photos');
    const savedVideos = localStorage.getItem('portfolio_videos');

    if (savedPhotos) {
      try {
        setPhotos(JSON.parse(savedPhotos));
      } catch (e) {
        console.error('Error loading photos:', e);
      }
    }
    if (savedVideos) {
      try {
        setVideos(JSON.parse(savedVideos));
      } catch (e) {
        console.error('Error loading videos:', e);
      }
    }
  }, []);

  // Compress and convert image to base64
  const compressImage = (file, maxWidth = 1200, quality = 0.8) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = (e) => {
        const img = new Image();
        img.src = e.target.result;
        img.onload = () => {
          const canvas = document.createElement('canvas');
          let width = img.width;
          let height = img.height;

          // Scale down if too large
          if (width > maxWidth) {
            height = (height * maxWidth) / width;
            width = maxWidth;
          }

          canvas.width = width;
          canvas.height = height;

          const ctx = canvas.getContext('2d');
          ctx.drawImage(img, 0, 0, width, height);

          // Convert to compressed base64
          const compressedBase64 = canvas.toDataURL('image/jpeg', quality);
          resolve(compressedBase64);
        };
        img.onerror = reject;
      };
      reader.onerror = reject;
    });
  };

  const handleFileUpload = async (e, type) => {
    const files = Array.from(e.target.files);
    if (files.length === 0) return;

    setUploading(true);

    try {
      const newItems = [];

      for (const file of files) {
        // Validate file type
        if (type === 'photos' && !file.type.startsWith('image/')) {
          toast.error(`${file.name} is not an image file`);
          continue;
        }
        if (type === 'videos' && !file.type.startsWith('video/')) {
          toast.error(`${file.name} is not a video file`);
          continue;
        }

        // Compress and convert to base64 for persistent storage
        const base64Url = await compressImage(file);

        const newItem = {
          id: Date.now() + Math.random(),
          name: file.name,
          url: base64Url,
          category: 'portrait',
          title: file.name.replace(/\.[^/.]+$/, ''),
          uploadedAt: new Date().toISOString(),
          position: 'center center',
        };

        newItems.push(newItem);
      }

      if (newItems.length > 0) {
        if (type === 'photos') {
          const updatedPhotos = [...photos, ...newItems];
          setPhotos(updatedPhotos);
          localStorage.setItem('portfolio_photos', JSON.stringify(updatedPhotos));
        } else {
          const updatedVideos = [...videos, ...newItems];
          setVideos(updatedVideos);
          localStorage.setItem('portfolio_videos', JSON.stringify(updatedVideos));
        }
        toast.success(`${newItems.length} file(s) uploaded successfully`);
      }
    } catch (error) {
      toast.error('Error uploading files');
      console.error(error);
    } finally {
      setUploading(false);
      e.target.value = '';
    }
  };

  const handleDelete = (id, type) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      if (type === 'photos') {
        const updated = photos.filter(p => p.id !== id);
        setPhotos(updated);
        localStorage.setItem('portfolio_photos', JSON.stringify(updated));
      } else {
        const updated = videos.filter(v => v.id !== id);
        setVideos(updated);
        localStorage.setItem('portfolio_videos', JSON.stringify(updated));
      }
      toast.success('Item deleted');
    }
  };

  const handleEdit = (item, type) => {
    setEditingItem({ ...item, type });
  };

  const handleSaveEdit = () => {
    if (!editingItem) return;

    if (editingItem.type === 'photos') {
      const updated = photos.map(p => 
        p.id === editingItem.id 
          ? { ...p, title: editingItem.title, category: editingItem.category, position: editingItem.position || 'center center' } 
          : p
      );
      setPhotos(updated);
      localStorage.setItem('portfolio_photos', JSON.stringify(updated));
    } else {
      const updated = videos.map(v => 
        v.id === editingItem.id 
          ? { ...v, title: editingItem.title, category: editingItem.category, position: editingItem.position || 'center center' } 
          : v
      );
      setVideos(updated);
      localStorage.setItem('portfolio_videos', JSON.stringify(updated));
    }

    setEditingItem(null);
    toast.success('Item updated');
  };

  const getPositionValues = (position) => {
    if (!position) return { left: '50%', top: '50%' };

    const parts = position.split(' ');
    let left = '50%';
    let top = '50%';

    if (parts[0]) {
      if (parts[0] === 'left') left = '0%';
      else if (parts[0] === 'right') left = '100%';
      else if (parts[0] === 'center') left = '50%';
      else left = parts[0];
    }

    if (parts[1]) {
      if (parts[1] === 'top') top = '0%';
      else if (parts[1] === 'bottom') top = '100%';
      else if (parts[1] === 'center') top = '50%';
      else top = parts[1];
    }

    return { left, top };
  };

  return (
    <div className="admin-page">
      <div className="admin-header">
        <h1>Admin Panel</h1>
        <p>Manage your portfolio photos and videos</p>
      </div>

      <div className="admin-tabs">
        <button
          className={`tab-button ${activeTab === 'photos' ? 'active' : ''}`}
          onClick={() => setActiveTab('photos')}
        >
          <FiImage /> Photos
        </button>
        <button
          className={`tab-button ${activeTab === 'videos' ? 'active' : ''}`}
          onClick={() => setActiveTab('videos')}
        >
          <FiVideo /> Videos
        </button>
      </div>

      <div className="admin-content">
        <div className="upload-section">
          <label className="upload-button">
            <FiUpload />
            Upload {activeTab === 'photos' ? 'Photos' : 'Videos'}
            <input
              type="file"
              multiple
              accept={activeTab === 'photos' ? 'image/*' : 'video/*'}
              onChange={(e) => handleFileUpload(e, activeTab)}
              disabled={uploading}
              style={{ display: 'none' }}
            />
          </label>
          {uploading && <p className="upload-status">Uploading...</p>}
        </div>

        <div className="media-grid">
          {(activeTab === 'photos' ? photos : videos).map((item) => (
            <div key={item.id} className="media-item">
              {activeTab === 'photos' ? (
                <img 
                  src={item.url} 
                  alt={item.title} 
                  style={{ objectPosition: item.position || 'center center' }}
                />
              ) : (
                <video src={item.url} muted />
              )}
              <div className="media-overlay">
                <div className="media-info">
                  <h3>{item.title}</h3>
                  <p>{item.category}</p>
                </div>
                <div className="media-actions">
                  <button onClick={() => handleEdit(item, activeTab)} className="action-btn edit">
                    <FiEdit2 />
                  </button>
                  <button onClick={() => handleDelete(item.id, activeTab)} className="action-btn delete">
                    <FiTrash2 />
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {(activeTab === 'photos' ? photos : videos).length === 0 && (
          <div className="empty-state">
            <p>No {activeTab} uploaded yet. Click the upload button to get started.</p>
          </div>
        )}
      </div>

      {editingItem && (
        <div className="edit-modal">
          <div className="modal-content">
            <button className="modal-close" onClick={() => setEditingItem(null)}>
              <FiX />
            </button>
            <h2>Edit {editingItem.type === 'photos' ? 'Photo' : 'Video'}</h2>

            {/* Visual Position Picker */}
            {editingItem.type === 'photos' && (
              <div className="form-group">
                <label>Focal Point (click to set where the image should center)</label>
                <div className="position-picker">
                  <div 
                    className="position-preview"
                    onClick={(e) => {
                      const rect = e.currentTarget.getBoundingClientRect();
                      const x = Math.round(((e.clientX - rect.left) / rect.width) * 100);
                      const y = Math.round(((e.clientY - rect.top) / rect.height) * 100);
                      setEditingItem({ ...editingItem, position: `${x}% ${y}%` });
                    }}
                  >
                    <img 
                      src={editingItem.url} 
                      alt="Preview" 
                      style={{ objectPosition: editingItem.position || 'center center' }}
                    />
                    <div 
                      className="position-marker"
                      style={getPositionValues(editingItem.position)}
                    ></div>
                  </div>
                  <div className="position-presets">
                    <button 
                      type="button" 
                      className={editingItem.position === 'left top' ? 'active' : ''}
                      onClick={() => setEditingItem({ ...editingItem, position: 'left top' })}
                    >↖</button>
                    <button 
                      type="button"
                      className={editingItem.position === 'center top' ? 'active' : ''}
                      onClick={() => setEditingItem({ ...editingItem, position: 'center top' })}
                    >↑</button>
                    <button 
                      type="button"
                      className={editingItem.position === 'right top' ? 'active' : ''}
                      onClick={() => setEditingItem({ ...editingItem, position: 'right top' })}
                    >↗</button>
                    <button 
                      type="button"
                      className={editingItem.position === 'left center' ? 'active' : ''}
                      onClick={() => setEditingItem({ ...editingItem, position: 'left center' })}
                    >←</button>
                    <button 
                      type="button"
                      className={editingItem.position === 'center center' ? 'active' : ''}
                      onClick={() => setEditingItem({ ...editingItem, position: 'center center' })}
                    >●</button>
                    <button 
                      type="button"
                      className={editingItem.position === 'right center' ? 'active' : ''}
                      onClick={() => setEditingItem({ ...editingItem, position: 'right center' })}
                    >→</button>
                    <button 
                      type="button"
                      className={editingItem.position === 'left bottom' ? 'active' : ''}
                      onClick={() => setEditingItem({ ...editingItem, position: 'left bottom' })}
                    >↙</button>
                    <button 
                      type="button"
                      className={editingItem.position === 'center bottom' ? 'active' : ''}
                      onClick={() => setEditingItem({ ...editingItem, position: 'center bottom' })}
                    >↓</button>
                    <button 
                      type="button"
                      className={editingItem.position === 'right bottom' ? 'active' : ''}
                      onClick={() => setEditingItem({ ...editingItem, position: 'right bottom' })}
                    >↘</button>
                  </div>
                </div>
              </div>
            )}

            <div className="form-group">
              <label>Title</label>
              <input
                type="text"
                value={editingItem.title}
                onChange={(e) => setEditingItem({ ...editingItem, title: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>Category</label>
              <select
                value={editingItem.category}
                onChange={(e) => setEditingItem({ ...editingItem, category: e.target.value })}
              >
                <option value="portrait">Portrait</option>
                <option value="wedding">Wedding</option>
                <option value="commercial">Commercial</option>
                <option value="event">Event</option>
              </select>
            </div>
            <button className="save-button" onClick={handleSaveEdit}>
              Save Changes
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Admin;
