import React, { useEffect, useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import { FiArrowDown } from 'react-icons/fi';
import './Home.css';

// Import actual photos
import photo1 from '../Photos/DSC00390.jpg';
import photo2 from '../Photos/DSC04635.jpg';
import photo3 from '../Photos/DSC04724.jpg';
import photo4 from '../Photos/DSC04875.jpg';

const Home = () => {
  const [scrollY, setScrollY] = useState(0);
  const [isVisible, setIsVisible] = useState({});
  const sectionRefs = useRef({});

  const images = [photo1, photo2, photo3, photo4];

  useEffect(() => {
    // Scroll-based animations
    const handleScroll = () => {
      setScrollY(window.scrollY);

      // Calculate visibility based on scroll position
      Object.keys(sectionRefs.current).forEach((key) => {
        const ref = sectionRefs.current[key];
        if (ref) {
          const rect = ref.getBoundingClientRect();
          const windowHeight = window.innerHeight;
          const isInView = rect.top < windowHeight * 0.8 && rect.bottom > windowHeight * 0.2;

          setIsVisible((prev) => ({
            ...prev,
            [key]: isInView,
          }));
        }
      });
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    handleScroll(); // Initial check

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    // Intersection Observer for initial animations
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsVisible((prev) => ({
              ...prev,
              [entry.target.dataset.section]: true,
            }));
          }
        });
      },
      { threshold: 0.1, rootMargin: '0px' }
    );

    Object.values(sectionRefs.current).forEach((ref) => {
      if (ref) observer.observe(ref);
    });

    return () => observer.disconnect();
  }, []);

  return (
    <div className="home">
      {/* Cinematic Hero Section */}
      <section className="hero-section">
        <div className="hero-images">
          {images.map((img, index) => (
            <div
              key={index}
              className={`hero-image ${index === currentImageIndex ? 'active' : ''}`}
              style={{ backgroundImage: `url(${img})` }}
            />
          ))}
        </div>
        <div className="hero-overlay"></div>
        <div className="hero-content">
          <h1 className="hero-title">
            <span className="hero-line">PHOTOGRAPHY</span>
            <span className="hero-line">PORTFOLIO</span>
          </h1>
          <p className="hero-subtitle">
            Capturing moments with artistry and precision
          </p>
          <div className="hero-actions">
            <Link to="/gallery" className="hero-link">
              View Work
              <FiArrowRight />
            </Link>
            <Link to="/booking" className="hero-link secondary">
              Book Session
            </Link>
          </div>
        </div>
        <div className="hero-scroll">
          <span>Scroll</span>
          <FiArrowDown />
        </div>
        <div className="hero-indicators">
          {images.map((_, index) => (
            <button
              key={index}
              className={`indicator ${index === currentImageIndex ? 'active' : ''}`}
              onClick={() => setCurrentImageIndex(index)}
              aria-label={`Go to image ${index + 1}`}
            />
          ))}
        </div>
      </section>

      {/* Full-width Image Sections */}
      <section
        className="image-section"
        data-section="image1"
        ref={(el) => (sectionRefs.current['image1'] = el)}
      >
        <div className={`image-container ${isVisible['image1'] ? 'visible' : ''}`}>
          <img src={photo1} alt="Photography work" loading="lazy" />
          <div className="image-overlay">
            <div className="image-content">
              <h2 className="image-title">Portrait</h2>
              <p className="image-description">Capturing the essence of personality</p>
            </div>
          </div>
        </div>
      </section>

      <section
        className="image-section"
        data-section="image2"
        ref={(el) => (sectionRefs.current['image2'] = el)}
      >
        <div className={`image-container ${isVisible['image2'] ? 'visible' : ''}`}>
          <img src={photo2} alt="Photography work" loading="lazy" />
          <div className="image-overlay">
            <div className="image-content">
              <h2 className="image-title">Storytelling</h2>
              <p className="image-description">Every frame tells a story</p>
            </div>
          </div>
        </div>
      </section>

      <section
        className="image-section"
        data-section="image3"
        ref={(el) => (sectionRefs.current['image3'] = el)}
      >
        <div className={`image-container ${isVisible['image3'] ? 'visible' : ''}`}>
          <img src={photo3} alt="Photography work" loading="lazy" />
          <div className="image-overlay">
            <div className="image-content">
              <h2 className="image-title">Artistry</h2>
              <p className="image-description">Where vision meets reality</p>
            </div>
          </div>
        </div>
      </section>

      <section
        className="image-section"
        data-section="image4"
        ref={(el) => (sectionRefs.current['image4'] = el)}
      >
        <div className={`image-container ${isVisible['image4'] ? 'visible' : ''}`}>
          <img src={photo4} alt="Photography work" loading="lazy" />
          <div className="image-overlay">
            <div className="image-content">
              <h2 className="image-title">Moments</h2>
              <p className="image-description">Preserving memories forever</p>
            </div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section
        className="cta-section"
        data-section="cta"
        ref={(el) => (sectionRefs.current['cta'] = el)}
      >
        <div className={`cta-content ${isVisible['cta'] ? 'visible' : ''}`}>
          <h2 className="cta-title">Ready to Create Something Beautiful?</h2>
          <p className="cta-text">
            Let's discuss your project and bring your vision to life
          </p>
          <Link to="/booking" className="cta-button">
            Book a Session
            <FiArrowRight />
          </Link>
        </div>
      </section>
    </div>
  );
};

export default Home;
