import React, { useState, useEffect, useRef } from 'react';
import { FiX, FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import './Gallery.css';

// Import actual photos
import photo1 from '../Photos/DSC00390.jpg';
import photo2 from '../Photos/DSC04635.jpg';
import photo3 from '../Photos/DSC04724.jpg';
import photo4 from '../Photos/DSC04875.jpg';

const Gallery = () => {
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [lightboxImage, setLightboxImage] = useState(null);
  const [lightboxIndex, setLightboxIndex] = useState(0);
  const [visibleItems, setVisibleItems] = useState(new Set());
  const itemRefs = useRef({});

  const categories = ['all', 'portrait', 'wedding', 'commercial', 'event'];
  const categoryLabels = {
    all: 'All Work',
    portrait: 'Portrait',
    wedding: 'Wedding',
    commercial: 'Commercial',
    event: 'Event'
  };

  // Use actual photos - you can duplicate them or add more
  const galleryItems = [
    { id: 1, category: 'portrait', image: photo1, title: 'Portrait Series' },
    { id: 2, category: 'wedding', image: photo2, title: 'Wedding Collection' },
    { id: 3, category: 'commercial', image: photo3, title: 'Commercial Work' },
    { id: 4, category: 'event', image: photo4, title: 'Event Photography' },
    { id: 5, category: 'portrait', image: photo1, title: 'Portrait Study' },
    { id: 6, category: 'wedding', image: photo2, title: 'Wedding Moments' },
    { id: 7, category: 'commercial', image: photo3, title: 'Brand Photography' },
    { id: 8, category: 'event', image: photo4, title: 'Event Coverage' },
    { id: 9, category: 'portrait', image: photo1, title: 'Portrait Art' },
    { id: 10, category: 'wedding', image: photo2, title: 'Wedding Story' },
    { id: 11, category: 'commercial', image: photo3, title: 'Commercial Art' },
    { id: 12, category: 'event', image: photo4, title: 'Event Story' },
  ];

  const filteredItems = selectedCategory === 'all' 
    ? galleryItems 
    : galleryItems.filter(item => item.category === selectedCategory);

  useEffect(() => {
    // Reset visible items when filter changes
    setVisibleItems(new Set());

    // Intersection Observer for scroll animations
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setVisibleItems((prev) => new Set([...prev, entry.target.dataset.id]));
          }
        });
      },
      { threshold: 0.1, rootMargin: '50px' }
    );

    // Small delay to ensure refs are updated
    const timeoutId = setTimeout(() => {
      Object.values(itemRefs.current).forEach((ref) => {
        if (ref) observer.observe(ref);
      });
    }, 100);

    return () => {
      clearTimeout(timeoutId);
      observer.disconnect();
    };
  }, [filteredItems]);

  const openLightbox = (index) => {
    setLightboxIndex(index);
    setLightboxImage(filteredItems[index]);
    document.body.style.overflow = 'hidden';
  };

  const closeLightbox = () => {
    setLightboxImage(null);
    document.body.style.overflow = 'unset';
  };

  const navigateLightbox = (direction) => {
    let newIndex = lightboxIndex + direction;
    if (newIndex < 0) newIndex = filteredItems.length - 1;
    if (newIndex >= filteredItems.length) newIndex = 0;
    setLightboxIndex(newIndex);
    setLightboxImage(filteredItems[newIndex]);
  };

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (!lightboxImage) return;
      if (e.key === 'Escape') closeLightbox();
      if (e.key === 'ArrowLeft') navigateLightbox(-1);
      if (e.key === 'ArrowRight') navigateLightbox(1);
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [lightboxImage, lightboxIndex, filteredItems]);

  return (
    <div className="gallery-page">
      <div className="gallery-header">
        <div className="container">
          <h1 className="gallery-title">GALLERY</h1>
          <p className="gallery-subtitle">A collection of my finest work</p>
        </div>
      </div>

      <div className="gallery-filters">
        <div className="container">
          <div className="filter-buttons">
            {categories.map(category => (
              <button
                key={category}
                className={`filter-btn ${selectedCategory === category ? 'active' : ''}`}
                onClick={() => setSelectedCategory(category)}
              >
                {categoryLabels[category]}
              </button>
            ))}
          </div>
        </div>
      </div>

      <div className="gallery-container">
        <div className="gallery-grid">
          {filteredItems.map((item, index) => (
            <div
              key={item.id}
              ref={(el) => (itemRefs.current[item.id] = el)}
              data-id={item.id}
              className={`gallery-item ${visibleItems.has(String(item.id)) ? 'visible' : ''}`}
              onClick={() => openLightbox(index)}
            >
              <div className="gallery-image-wrapper">
                <img 
                  src={item.image} 
                  alt={item.title}
                  className="gallery-image"
                  loading="lazy"
                />
                <div className="gallery-overlay">
                  <span className="gallery-category">{categoryLabels[item.category]}</span>
                  <span className="gallery-title-text">{item.title}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {lightboxImage && (
        <div className="lightbox" onClick={closeLightbox}>
          <button className="lightbox-close" onClick={closeLightbox} aria-label="Close">
            <FiX />
          </button>
          <button 
            className="lightbox-nav lightbox-prev"
            onClick={(e) => {
              e.stopPropagation();
              navigateLightbox(-1);
            }}
            aria-label="Previous image"
          >
            <FiChevronLeft />
          </button>
          <button 
            className="lightbox-nav lightbox-next"
            onClick={(e) => {
              e.stopPropagation();
              navigateLightbox(1);
            }}
            aria-label="Next image"
          >
            <FiChevronRight />
          </button>
          <div className="lightbox-content" onClick={(e) => e.stopPropagation()}>
            <div className="lightbox-image-container">
              <img 
                src={lightboxImage.image} 
                alt={lightboxImage.title}
                className="lightbox-image"
              />
            </div>
            <div className="lightbox-info">
              <h3>{lightboxImage.title}</h3>
              <p>{categoryLabels[lightboxImage.category]}</p>
              <span className="lightbox-counter">
                {lightboxIndex + 1} / {filteredItems.length}
              </span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Gallery;
