import React, { useEffect, useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import { FiArrowDown } from 'react-icons/fi';
import './Home.css';

// Import actual photos
import photo1 from '../Photos/DSC00390.jpg';
import photo2 from '../Photos/DSC04635.jpg';
import photo3 from '../Photos/DSC04724.jpg';
import photo4 from '../Photos/DSC04875.jpg';

const Home = () => {
  const [scrollY, setScrollY] = useState(0);
  const [isVisible, setIsVisible] = useState({});
  const sectionRefs = useRef({});

  const images = [photo1, photo2, photo3, photo4];

  useEffect(() => {
    // Scroll-based animations
    const handleScroll = () => {
      setScrollY(window.scrollY);

      // Calculate visibility based on scroll position
      Object.keys(sectionRefs.current).forEach((key) => {
        const ref = sectionRefs.current[key];
        if (ref) {
          const rect = ref.getBoundingClientRect();
          const windowHeight = window.innerHeight;
          const isInView = rect.top < windowHeight * 0.8 && rect.bottom > windowHeight * 0.2;

          setIsVisible((prev) => ({
            ...prev,
            [key]: isInView,
          }));
        }
      });
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    handleScroll(); // Initial check

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    // Intersection Observer for initial animations
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsVisible((prev) => ({
              ...prev,
              [entry.target.dataset.section]: true,
            }));
          }
        });
      },
      { threshold: 0.1, rootMargin: '0px' }
    );

    Object.values(sectionRefs.current).forEach((ref) => {
      if (ref) observer.observe(ref);
    });

    return () => observer.disconnect();
  }, []);

  return (
    <div className="home">
      {/* Minimal Hero Section */}
      <section className="hero-section" ref={(el) => (sectionRefs.current['hero'] = el)}>
        <div className="hero-image" style={{ backgroundImage: `url(${images[0]})` }}></div>
        <div className="hero-overlay"></div>
        <div className={`hero-content ${isVisible['hero'] ? 'visible' : ''}`}>
          <h1 className="hero-title">
            <span className="hero-line">PHOTOGRAPHY</span>
            <span className="hero-line">PORTFOLIO</span>
          </h1>
        </div>
        <div className={`hero-scroll ${scrollY > 100 ? 'fade-out' : ''}`}>
          <span>Scroll</span>
          <FiArrowDown />
        </div>
      </section>

      {/* Full-width Image Sections with Scroll Animations */}
      <section
        className="image-section"
        data-section="image1"
        ref={(el) => (sectionRefs.current['image1'] = el)}
      >
        <div className="image-container">
          <div 
            className="image-parallax"
            style={{ 
              transform: `translateY(${scrollY * 0.3}px)`,
              opacity: isVisible['image1'] ? 1 : 0.3
            }}
          >
            <img src={photo1} alt="Photography work" loading="lazy" />
          </div>
          <div className={`image-overlay ${isVisible['image1'] ? 'visible' : ''}`}>
            <div className="image-content">
              <h2 className="image-title">
                <span className="title-line">PORTRAIT</span>
              </h2>
            </div>
          </div>
        </div>
      </section>

      <section
        className="image-section"
        data-section="image2"
        ref={(el) => (sectionRefs.current['image2'] = el)}
      >
        <div className="image-container">
          <div 
            className="image-parallax"
            style={{ 
              transform: `translateY(${Math.max(0, scrollY - window.innerHeight) * 0.3}px)`,
              opacity: isVisible['image2'] ? 1 : 0.3
            }}
          >
            <img src={photo2} alt="Photography work" loading="lazy" />
          </div>
          <div className={`image-overlay ${isVisible['image2'] ? 'visible' : ''}`}>
            <div className="image-content">
              <h2 className="image-title">
                <span className="title-line">STORYTELLING</span>
              </h2>
            </div>
          </div>
        </div>
      </section>

      <section
        className="image-section"
        data-section="image3"
        ref={(el) => (sectionRefs.current['image3'] = el)}
      >
        <div className="image-container">
          <div 
            className="image-parallax"
            style={{ 
              transform: `translateY(${Math.max(0, scrollY - window.innerHeight * 2) * 0.3}px)`,
              opacity: isVisible['image3'] ? 1 : 0.3
            }}
          >
            <img src={photo3} alt="Photography work" loading="lazy" />
          </div>
          <div className={`image-overlay ${isVisible['image3'] ? 'visible' : ''}`}>
            <div className="image-content">
              <h2 className="image-title">
                <span className="title-line">ARTISTRY</span>
              </h2>
            </div>
          </div>
        </div>
      </section>

      <section
        className="image-section"
        data-section="image4"
        ref={(el) => (sectionRefs.current['image4'] = el)}
      >
        <div className="image-container">
          <div 
            className="image-parallax"
            style={{ 
              transform: `translateY(${Math.max(0, scrollY - window.innerHeight * 3) * 0.3}px)`,
              opacity: isVisible['image4'] ? 1 : 0.3
            }}
          >
            <img src={photo4} alt="Photography work" loading="lazy" />
          </div>
          <div className={`image-overlay ${isVisible['image4'] ? 'visible' : ''}`}>
            <div className="image-content">
              <h2 className="image-title">
                <span className="title-line">MOMENTS</span>
              </h2>
            </div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section
        className="cta-section"
        data-section="cta"
        ref={(el) => (sectionRefs.current['cta'] = el)}
      >
        <div className={`cta-content ${isVisible['cta'] ? 'visible' : ''}`}>
          <h2 className="cta-title">
            <span className="cta-line">READY TO</span>
            <span className="cta-line">CREATE</span>
          </h2>
          <Link to="/booking" className="cta-link">Book Session</Link>
          <Link to="/gallery" className="cta-link secondary">View Gallery</Link>
        </div>
      </section>
    </div>
  );
};

export default Home;
